import { useState } from 'react';

export default function Ingredients() {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedImage, setSelectedImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [analysisResults, setAnalysisResults] = useState(null);
  const [chatMessages, setChatMessages] = useState([]);
  const [userMessage, setUserMessage] = useState('');
  
  const handleImageChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];
      setSelectedImage(file);
      
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };
  
  const handleAnalyzeImage = () => {
    if (!selectedImage) return;
    
    setIsAnalyzing(true);
    
    setTimeout(() => {
      // Simulated analysis results
      const ingredientsList = [
        "Water (Aqua)",
        "Sodium Laureth Sulfate",
        "Cocamidopropyl Betaine",
        "Glycerin",
        "Fragrance (Parfum)",
        "Sodium Chloride",
        "Citric Acid",
        "Parabens",
        "Methylisothiazolinone"
      ];
      
      const potentiallyHarmful = [
        {
          name: "Sodium Laureth Sulfate",
          concerns: "May cause skin irritation in some individuals, particularly those with sensitive skin."
        },
        {
          name: "Parabens",
          concerns: "Some studies suggest potential endocrine disruption. Controversial ingredient."
        },
        {
          name: "Methylisothiazolinone",
          concerns: "Known skin sensitizer, can cause allergic reactions."
        }
      ];
      
      setAnalysisResults({
        ingredientsList,
        potentiallyHarmful,
        message: "We've identified the ingredients in your product. Some ingredients may be of concern."
      });
      
      // Add initial message to chat
      setChatMessages([
        { 
          sender: 'system', 
          text: 'Ingredients analysis complete. You can ask me questions about these ingredients or any other beauty product ingredients.'
        }
      ]);
      
      setIsAnalyzing(false);
    }, 2000);
  };
  
  const handleClearImage = () => {
    setSelectedImage(null);
    setImagePreview(null);
    setAnalysisResults(null);
    setChatMessages([]);
  };
  
  const handleChatSubmit = (e) => {
    e.preventDefault();
    if (!userMessage.trim()) return;
    
    // Add user message to chat
    const newMessages = [
      ...chatMessages,
      { sender: 'user', text: userMessage }
    ];
    setChatMessages(newMessages);
    
    // Simulate Gemini response
    setTimeout(() => {
      let aiResponse = "I'm analyzing your query...";
      
      // Simulate intelligent responses based on keywords
      if (userMessage.toLowerCase().includes("parabens")) {
        aiResponse = "Parabens are preservatives commonly used in cosmetics and personal care products. They prevent the growth of harmful bacteria and mold. However, some studies suggest they may disrupt hormones in the body, mimicking estrogen. The scientific community is divided on their safety, but they're being phased out of many products as a precaution.";
      } else if (userMessage.toLowerCase().includes("sulfate")) {
        aiResponse = "Sodium Laureth Sulfate (SLES) is a surfactant that creates lather in cleansing products. It's effective at removing oil and dirt but can be drying or irritating for some people, especially those with sensitive skin or conditions like eczema. Sulfate-free alternatives are increasingly popular for gentler cleansing.";
      } else if (userMessage.toLowerCase().includes("fragrance") || userMessage.toLowerCase().includes("parfum")) {
        aiResponse = "Fragrance or parfum in ingredient lists can actually represent dozens or even hundreds of undisclosed chemicals. Companies aren't required to disclose the specific components of their fragrances. This can be problematic for people with sensitivities or allergies. Fragrance is one of the most common causes of allergic reactions to cosmetics.";
      } else if (userMessage.toLowerCase().includes("natural") || userMessage.toLowerCase().includes("organic")) {
        aiResponse = "Natural and organic ingredients aren't necessarily safer than synthetic ones. Both natural and synthetic ingredients can cause irritation or allergic reactions. The safety of an ingredient depends on many factors including concentration, formulation, and individual sensitivity. It's also worth noting that terms like 'natural' aren't strictly regulated in cosmetics.";
      } else {
        aiResponse = "If you have specific ingredients you're curious about, I can provide information about their function, potential benefits, and any safety concerns. You can also ask about ingredient types like preservatives, surfactants, or humectants to learn more about their roles in cosmetic formulations.";
      }
      
      setChatMessages([
        ...newMessages,
        { sender: 'ai', text: aiResponse }
      ]);
    }, 1000);
    
    setUserMessage('');
  };
  
  return (
    <section className="ingredients-container">
      <h1>Ingredient Checker</h1>
      <p>Search for ingredients or upload a product image to check what's in your beauty products.</p>
      
      <div className="search-container">
        <input 
          type="text" 
          className="search-box" 
          placeholder="Search for an ingredient..." 
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>
      
      <div className="upload-section">
        <h2>Or Upload a Product Image</h2>
        <p>Take a photo of your product's ingredient list and we'll analyze it for you.</p>
        
        <div className="upload-container">
          {!imagePreview ? (
            <div className="upload-box">
              <label htmlFor="image-upload" className="upload-label">
                <div className="upload-icon">
                  <svg xmlns="http://www.w3.org/2000/svg" width="48" height="48" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                    <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"></path>
                    <polyline points="17 8 12 3 7 8"></polyline>
                    <line x1="12" y1="3" x2="12" y2="15"></line>
                  </svg>
                </div>
                <p>Click to upload an image</p>
              </label>
              <input 
                type="file" 
                id="image-upload" 
                accept="image/*" 
                onChange={handleImageChange} 
                style={{ display: 'none' }}
              />
            </div>
          ) : (
            <div className="image-preview-container">
              <img src={imagePreview} alt="Preview" className="image-preview" />
              <div className="preview-actions">
                <button 
                  className="analyze-btn" 
                  onClick={handleAnalyzeImage}
                  disabled={isAnalyzing}
                >
                  {isAnalyzing ? 'Analyzing...' : 'Analyze Ingredients'}
                </button>
                <button className="clear-btn" onClick={handleClearImage}>
                  Clear
                </button>
              </div>
            </div>
          )}
        </div>
        
        {analysisResults && (
          <div className="analysis-results-container">
            <div className="analysis-results">
              <h3>Analysis Results</h3>
              <p>{analysisResults.message}</p>
              
              {analysisResults.ingredientsList && (
                <div className="ingredients-list">
                  <h4>Ingredients Found:</h4>
                  <ul>
                    {analysisResults.ingredientsList.map((ingredient, index) => (
                      <li key={index}>{ingredient}</li>
                    ))}
                  </ul>
                </div>
              )}
              
              {analysisResults.potentiallyHarmful && (
                <div className="harmful-ingredients">
                  <h4>Potentially Concerning Ingredients:</h4>
                  <ul className="concern-list">
                    {analysisResults.potentiallyHarmful.map((item, index) => (
                      <li key={index} className="concern-item">
                        <span className="concern-name">{item.name}</span>
                        <p className="concern-details">{item.concerns}</p>
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
            
            <div className="chat-container">
              <h3>Ask Ingredient Assistant</h3>
              <div className="chat-messages">
                {chatMessages.map((msg, index) => (
                  <div key={index} className={`message ${msg.sender}`}>
                    <p>{msg.text}</p>
                  </div>
                ))}
              </div>
              
              <form onSubmit={handleChatSubmit} className="chat-input">
                <input
                  type="text"
                  value={userMessage}
                  onChange={(e) => setUserMessage(e.target.value)}
                  placeholder="Ask about ingredients..."
                  className="chat-text-input"
                />
                <button type="submit" className="chat-send-btn">Send</button>
              </form>
            </div>
          </div>
        )}
      </div>
    </section>
  );
}
