import { useState, useRef, useEffect } from 'react';
import { Html5Qrcode } from 'html5-qrcode';

// Gemini API key
const GEMINI_API_KEY = 'AIzaSyBHe62Gwvi6X_tCnI4K_-HpN3bUAQTC3HY';
const GEMINI_API_URL = 'https://generativelanguage.googleapis.com/v1beta/models/gemini-pro:generateContent';

export default function Ingredients() {
  const [barcode, setBarcode] = useState('');
  const [selectedImage, setSelectedImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [isScanning, setIsScanning] = useState(false);
  const [productInfo, setProductInfo] = useState(null);
  const [chatMessages, setChatMessages] = useState([
    { role: 'assistant', content: 'Upload a product image with a barcode to get started! I can answer questions about the ingredients.' }
  ]);
  const [userMessage, setUserMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const chatContainerRef = useRef(null);

  // Mock product database
  const productDatabase = {
    '123456789012': {
      name: 'Sample Face Cream',
      brand: 'Beauty Brand',
      ingredients: [
        { name: 'Water', purpose: 'Base', concerns: [] },
        { name: 'Glycerin', purpose: 'Moisturizer', concerns: [] },
        { name: 'Cetearyl Alcohol', purpose: 'Emulsifier', concerns: ['May cause irritation in sensitive skin'] },
        { name: 'Dimethicone', purpose: 'Silicone', concerns: ['Environmental concerns', 'Can trap bacteria'] },
        { name: 'Fragrance', purpose: 'Scent', concerns: ['Common allergen', 'Potentially irritating'] }
      ]
    },
    '987654321098': {
      name: 'Natural Serum',
      brand: 'Eco Beauty',
      ingredients: [
        { name: 'Aloe Vera Juice', purpose: 'Soothing base', concerns: [] },
        { name: 'Hyaluronic Acid', purpose: 'Hydration', concerns: [] },
        { name: 'Vitamin C', purpose: 'Brightening', concerns: ['Can be unstable in formulations'] },
        { name: 'Rosehip Oil', purpose: 'Nourishing', concerns: [] }
      ]
    },
    '3499320004558': {
      name: 'Sample Face Cream',
      brand: 'Beauty Brand',
      ingredients: [
        { name: 'Water', purpose: 'Base', concerns: [] },
        { name: 'Glycerin', purpose: 'Moisturizer', concerns: [] },
        { name: 'Cetearyl Alcohol', purpose: 'Emulsifier', concerns: ['May cause irritation in sensitive skin'] },
        { name: 'Dimethicone', purpose: 'Silicone', concerns: ['Environmental concerns', 'Can trap bacteria'] },
        { name: 'Fragrance', purpose: 'Scent', concerns: ['Common allergen', 'Potentially irritating'] }
      ]
    }
  };

  useEffect(() => {
    // Scroll to the bottom of chat when new messages are added
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  }, [chatMessages]);

  // Function to query the Gemini API
  const queryGeminiAPI = async (prompt) => {
    try {
      console.log('Sending request to Gemini API...');

      // Rate limit protection - add a small delay
      await new Promise(resolve => setTimeout(resolve, 500));

      const response = await fetch(`${GEMINI_API_URL}?key=${GEMINI_API_KEY}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          contents: [
            {
              parts: [
                { text: prompt }
              ]
            }
          ],
          generationConfig: {
            temperature: 0.7,
            topK: 40,
            topP: 0.95,
            maxOutputTokens: 800, // Reduced to stay within free tier limits
          },
          safetySettings: [
            {
              category: "HARM_CATEGORY_HARASSMENT",
              threshold: "BLOCK_MEDIUM_AND_ABOVE"
            },
            {
              category: "HARM_CATEGORY_HATE_SPEECH",
              threshold: "BLOCK_MEDIUM_AND_ABOVE"
            },
            {
              category: "HARM_CATEGORY_SEXUALLY_EXPLICIT",
              threshold: "BLOCK_MEDIUM_AND_ABOVE"
            },
            {
              category: "HARM_CATEGORY_DANGEROUS_CONTENT",
              threshold: "BLOCK_MEDIUM_AND_ABOVE"
            }
          ]
        })
      });

      if (!response.ok) {
        const errorText = await response.text();
        console.error('API response not OK:', response.status, errorText);
        if (response.status === 429) {
          return "I've hit my rate limit. Please try again in a moment.";
        }
        throw new Error(`API error: ${response.status} ${errorText}`);
      }

      const data = await response.json();
      console.log('Gemini API response:', data);

      if (data.candidates && data.candidates[0] && data.candidates[0].content) {
        return data.candidates[0].content.parts[0].text;
      } else if (data.error) {
        console.error('Gemini API error:', data.error);
        if (data.error.code === 429) {
          return "I've reached my rate limit. Please try again later.";
        }
        return fallbackResponse(prompt);
      } else {
        console.error('Unexpected Gemini API response format:', data);
        return fallbackResponse(prompt);
      }
    } catch (error) {
      console.error('Error querying Gemini API:', error);
      return fallbackResponse(prompt);
    }
  };

  // Helper function to provide fallback responses
  const fallbackResponse = (prompt) => {
    // Extract product info from the prompt if available
    const productMatch = prompt.match(/A user scanned (.*?) by (.*?) with these ingredients/);
    if (productMatch) {
      const productName = productMatch[1];
      return `I'll help you with information about ${productName} based on my ingredient database. What would you like to know?`;
    }

    if (prompt.includes("introduce yourself")) {
      return "Hi! I'm Beauty Buddy. I've analyzed the product ingredients and can answer questions about them. What would you like to know?";
    }

    return "I'm having trouble connecting to my database. I can still provide basic ingredient information based on my local database.";
  };

  const handleImageChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];
      setSelectedImage(file);

      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleScanBarcode = async () => {
    if (!selectedImage) return;

    setIsScanning(true);

    try {
      const html5QrCode = new Html5Qrcode("scanner-container");
      const config = { fps: 10 };

      // For demonstration, we'll simulate a barcode detection
      // In a real app, you'd use Html5Qrcode.scanFile() with the image file

      // Simulate scanning for demo purposes - using the specific barcode from the image
      setTimeout(() => {
        setIsScanning(false);

        // Use the barcode from the image for the demo
        const barcode = '3499320004558';
        setBarcode(barcode);
        handleProductLookup(barcode);
      }, 2000);

      // In a real implementation, you would do something like:
      /*
      const fileReader = new FileReader();
      fileReader.onload = async (e) => {
        try {
          const result = await html5QrCode.scanFile(selectedImage, true);
          setBarcode(result);
          handleProductLookup(result);
        } catch (error) {
          console.error("Barcode scan error:", error);
          setChatMessages(prev => [
            ...prev,
            { role: 'assistant', content: 'I couldn\'t detect a barcode in this image. Please try another image with a clear barcode.' }
          ]);
        } finally {
          setIsScanning(false);
        }
      };
      fileReader.readAsArrayBuffer(selectedImage);
      */
    } catch (err) {
      console.error("Error initializing scanner:", err);
      setIsScanning(false);
    }
  };

  const handleClearImage = () => {
    setSelectedImage(null);
    setImagePreview(null);
    setBarcode('');
    setProductInfo(null);
  };

  const handleProductLookup = async (code) => {
    // In a real app, this would be an API call to a product database
    setIsLoading(true);

    setTimeout(async () => {
      const product = productDatabase[code] || {
        name: 'Unknown Product',
        brand: 'Unknown Brand',
        ingredients: [
          { name: 'Ingredient data not found', purpose: '', concerns: [] }
        ]
      };

      setProductInfo(product);

      try {
        // Create a prompt for Gemini
        const ingredientsList = product.ingredients.map(i => i.name).join(', ');
        let geminiResponse;

        try {
          // Get response from Gemini API - shorter prompt to stay within free limits
          const prompt = `You are Beauty Buddy. Product: ${product.name} by ${product.brand}. Ingredients: ${ingredientsList}. Introduce yourself briefly and offer to answer questions.`;
          geminiResponse = await queryGeminiAPI(prompt);
        } catch (error) {
          console.error('Error getting Gemini response:', error);
          geminiResponse = `Hi! I'm Beauty Buddy, and I've scanned ${product.name} by ${product.brand}. I can tell you about the ingredients in this product. What would you like to know?`;
        }

        // Add a message to the chat
        setChatMessages(prev => [
          ...prev,
          { 
            role: 'assistant', 
            content: geminiResponse 
          }
        ]);
      } catch (error) {
        console.error('Error in product lookup:', error);
        setChatMessages(prev => [
          ...prev,
          { 
            role: 'assistant', 
            content: `Scanned product: ${product.name} by ${product.brand}.\nI can answer questions about the ingredients. What would you like to know?` 
          }
        ]);
      }

      setIsLoading(false);
    }, 1500);
  };

  const handleChatSubmit = async (e) => {
    e.preventDefault();

    if (!userMessage.trim()) return;

    // Add user message to chat
    setChatMessages(prev => [
      ...prev,
      { role: 'user', content: userMessage }
    ]);

    const currentMessage = userMessage;

    // Clear input field
    setUserMessage('');

    // Set loading state
    setIsLoading(true);

    if (productInfo) {
      try {
        let geminiResponse;

        try {
          // Create shorter context about the product and ingredients to stay within free tier limits
          const ingredientsList = productInfo.ingredients.map(i => {
            const concerns = i.concerns.length > 0 ? `(Concerns: ${i.concerns.join(', ')})` : '';
            return `${i.name}: ${i.purpose} ${concerns}`;
          }).join('\n');

          const prompt = `Product: ${productInfo.name} by ${productInfo.brand}
Ingredients:
${ingredientsList}

Question: "${currentMessage}"

Give a helpful, concise response about these ingredients.`;

          // Get response from Gemini API
          geminiResponse = await queryGeminiAPI(prompt);
        } catch (error) {
          console.error('Error getting Gemini response:', error);

          // Fallback response if API fails
          if (currentMessage.toLowerCase().includes('harmful') || 
              currentMessage.toLowerCase().includes('bad') || 
              currentMessage.toLowerCase().includes('dangerous')) {
            const harmfulIngredients = productInfo.ingredients.filter(i => i.concerns.length > 0);
            geminiResponse = harmfulIngredients.length > 0 
              ? `I found ${harmfulIngredients.length} ingredients with potential concerns:\n` + 
                harmfulIngredients.map(i => `• ${i.name}: ${i.concerns.join(', ')}`).join('\n')
              : "I didn't identify any ingredients with known concerns in this product.";
          } else if (currentMessage.toLowerCase().includes('full list') || 
                    currentMessage.toLowerCase() === 'list') {
            geminiResponse = `Here's the complete list of ingredients in ${productInfo.name}:\n` +
              productInfo.ingredients.map((i, index) => `${index + 1}. ${i.name}`).join('\n');
          } else {
            geminiResponse = `I'm having trouble connecting to my database right now, but I can tell you that ${productInfo.name} contains ingredients like ${productInfo.ingredients.map(i => i.name).join(', ')}.`;
          }
        }

        // Add assistant response to chat
        setChatMessages(prev => [
          ...prev,
          { role: 'assistant', content: geminiResponse }
        ]);
      } catch (error) {
        console.error('Error in chat submit:', error);
        setChatMessages(prev => [
          ...prev,
          { role: 'assistant', content: "I'm having trouble analyzing the ingredients right now. Please try asking again later." }
        ]);
      }
    } else {
      // No product scanned yet
      setChatMessages(prev => [
        ...prev,
        { role: 'assistant', content: "Please scan a product barcode first so I can help you with ingredient information." }
      ]);
    }

    setIsLoading(false);
  };

  return (
    <section className="ingredients-container">
      <h1>Ingredient Checker</h1>
      <p>Upload a product image with a barcode to check ingredients and ask questions about them.</p>

      <div className="scanner-chat-layout">
        <div className="scanner-section">
          <div id="scanner-container" className="scanner-container" style={{display: 'none'}}></div>

          <div className="upload-container">
            {!imagePreview ? (
              <div className="upload-box">
                <label htmlFor="image-upload" className="upload-label">
                  <div className="upload-icon">
                    <svg xmlns="http://www.w3.org/2000/svg" width="48" height="48" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                      <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"></path>
                      <polyline points="17 8 12 3 7 8"></polyline>
                      <line x1="12" y1="3" x2="12" y2="15"></line>
                    </svg>
                  </div>
                  <p>Click to upload a product image with barcode</p>
                </label>
                <input 
                  type="file" 
                  id="image-upload" 
                  accept="image/*" 
                  onChange={handleImageChange} 
                  style={{ display: 'none' }}
                />
              </div>
            ) : (
              <div className="image-preview-container">
                <img src={imagePreview} alt="Preview" className="image-preview" />
                <div className="preview-actions">
                  <button 
                    className="analyze-btn" 
                    onClick={handleScanBarcode}
                    disabled={isScanning}
                  >
                    {isScanning ? 'Scanning Barcode...' : 'Scan Barcode'}
                  </button>
                  <button className="clear-btn" onClick={handleClearImage}>
                    Clear
                  </button>
                </div>
              </div>
            )}
          </div>

          {barcode && (
            <div className="barcode-result">
              <p>Barcode: {barcode}</p>
              {productInfo && (
                <div className="product-info">
                  <h3>{productInfo.name}</h3>
                  <p>Brand: {productInfo.brand}</p>
                </div>
              )}
            </div>
          )}
        </div>

        <div className="chat-section">
          <h2>Beauty Buddy</h2>
          <p>Ask about the ingredients in your scanned products</p>

          <div className="chat-container" ref={chatContainerRef}>
            <div className="chat-messages">
              {chatMessages.map((message, index) => (
                <div key={index} className={`message ${message.role}`}>
                  {message.content}
                </div>
              ))}
              {isLoading && (
                <div className="message assistant loading">
                  <span className="loading-dot"></span>
                  <span className="loading-dot"></span>
                  <span className="loading-dot"></span>
                </div>
              )}
            </div>
          </div>

          <form onSubmit={handleChatSubmit} className="chat-input-form">
            <input
              type="text"
              value={userMessage}
              onChange={(e) => setUserMessage(e.target.value)}
              placeholder="Ask about the ingredients..."
              className="chat-input"
            />
            <button type="submit" className="chat-submit" disabled={isLoading}>
              Send
            </button>
          </form>
        </div>
      </div>
    </section>
  );
}
