import { useState, useRef, useEffect } from 'react';
import { Html5Qrcode } from 'html5-qrcode';

export default function Ingredients() {
  const [barcode, setBarcode] = useState('');
  const [selectedImage, setSelectedImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [isScanning, setIsScanning] = useState(false);
  const [productInfo, setProductInfo] = useState(null);
  const [chatMessages, setChatMessages] = useState([
    { role: 'assistant', content: 'Upload a product image with a barcode to get started! I can answer questions about the ingredients.' }
  ]);
  const [userMessage, setUserMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const chatContainerRef = useRef(null);

  // Mock product database
  const productDatabase = {
    '123456789012': {
      name: 'Sample Face Cream',
      brand: 'Beauty Brand',
      ingredients: [
        { name: 'Water', purpose: 'Base', concerns: [] },
        { name: 'Glycerin', purpose: 'Moisturizer', concerns: [] },
        { name: 'Cetearyl Alcohol', purpose: 'Emulsifier', concerns: ['May cause irritation in sensitive skin'] },
        { name: 'Dimethicone', purpose: 'Silicone', concerns: ['Environmental concerns', 'Can trap bacteria'] },
        { name: 'Fragrance', purpose: 'Scent', concerns: ['Common allergen', 'Potentially irritating'] }
      ]
    },
    '987654321098': {
      name: 'Natural Serum',
      brand: 'Eco Beauty',
      ingredients: [
        { name: 'Aloe Vera Juice', purpose: 'Soothing base', concerns: [] },
        { name: 'Hyaluronic Acid', purpose: 'Hydration', concerns: [] },
        { name: 'Vitamin C', purpose: 'Brightening', concerns: ['Can be unstable in formulations'] },
        { name: 'Rosehip Oil', purpose: 'Nourishing', concerns: [] }
      ]
    },
    '3499320004558': {
      name: 'Sample Face Cream',
      brand: 'Beauty Brand',
      ingredients: [
        { name: 'Water', purpose: 'Base', concerns: [] },
        { name: 'Glycerin', purpose: 'Moisturizer', concerns: [] },
        { name: 'Cetearyl Alcohol', purpose: 'Emulsifier', concerns: ['May cause irritation in sensitive skin'] },
        { name: 'Dimethicone', purpose: 'Silicone', concerns: ['Environmental concerns', 'Can trap bacteria'] },
        { name: 'Fragrance', purpose: 'Scent', concerns: ['Common allergen', 'Potentially irritating'] }
      ]
    }
  };

  useEffect(() => {
    // Scroll to the bottom of chat when new messages are added
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  }, [chatMessages]);

  const handleImageChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];
      setSelectedImage(file);

      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleScanBarcode = async () => {
    if (!selectedImage) return;

    setIsScanning(true);

    try {
      const html5QrCode = new Html5Qrcode("scanner-container");
      const config = { fps: 10 };

      // For demonstration, we'll simulate a barcode detection
      // In a real app, you'd use Html5Qrcode.scanFile() with the image file

      // Simulate scanning for demo purposes - using the specific barcode from the image
      setTimeout(() => {
        setIsScanning(false);

        // Use the barcode from the image for the demo
        const barcode = '3499320004558';
        setBarcode(barcode);
        handleProductLookup(barcode);
      }, 2000);

      // In a real implementation, you would do something like:
      /*
      const fileReader = new FileReader();
      fileReader.onload = async (e) => {
        try {
          const result = await html5QrCode.scanFile(selectedImage, true);
          setBarcode(result);
          handleProductLookup(result);
        } catch (error) {
          console.error("Barcode scan error:", error);
          setChatMessages(prev => [
            ...prev,
            { role: 'assistant', content: 'I couldn\'t detect a barcode in this image. Please try another image with a clear barcode.' }
          ]);
        } finally {
          setIsScanning(false);
        }
      };
      fileReader.readAsArrayBuffer(selectedImage);
      */
    } catch (err) {
      console.error("Error initializing scanner:", err);
      setIsScanning(false);
    }
  };

  const handleClearImage = () => {
    setSelectedImage(null);
    setImagePreview(null);
    setBarcode('');
    setProductInfo(null);
  };

  const handleProductLookup = (code) => {
    // In a real app, this would be an API call to a product database
    setIsLoading(true);

    setTimeout(() => {
      const product = productDatabase[code] || {
        name: 'Unknown Product',
        brand: 'Unknown Brand',
        ingredients: [
          { name: 'Ingredient data not found', purpose: '', concerns: [] }
        ]
      };

      setProductInfo(product);

      // Add a message to the chat
      setChatMessages(prev => [
        ...prev,
        { 
          role: 'assistant', 
          content: `Scanned product: ${product.name} by ${product.brand}.\nI can answer questions about the ingredients. What would you like to know?` 
        }
      ]);

      setIsLoading(false);
    }, 1500);
  };

  const handleChatSubmit = (e) => {
    e.preventDefault();

    if (!userMessage.trim()) return;

    // Add user message to chat
    setChatMessages(prev => [
      ...prev,
      { role: 'user', content: userMessage }
    ]);

    const currentMessage = userMessage;

    // Clear input field
    setUserMessage('');

    // Set loading state
    setIsLoading(true);

    // In a real implementation, this would be a call to the Gemini API
    // For now, we'll simulate a response with more variety
    setTimeout(() => {
      let response = "I don't have information about this product yet. Please try scanning another product.";

      if (productInfo) {
        // Check for repeat similar queries to avoid repetition
        const lastBotMessage = chatMessages.filter(msg => msg.role === 'assistant').pop()?.content || '';
        const isRepeatQuery = 
          (currentMessage.toLowerCase().includes('harmful') && lastBotMessage.includes('concerns')) ||
          (currentMessage.toLowerCase().includes('ingredient') && lastBotMessage.includes('ingredients:')) ||
          (currentMessage.toLowerCase().includes('purpose') && lastBotMessage.includes('what each ingredient does'));

        if (currentMessage.toLowerCase().includes('full list') || 
            currentMessage.toLowerCase() === 'full list' ||
            currentMessage.toLowerCase() === 'list') {
          // Handle "full list" command specially
          response = `Here's the complete list of ingredients in ${productInfo.name}:\n` +
            productInfo.ingredients.map((i, index) => `${index + 1}. ${i.name}`).join('\n');

        } else if (isRepeatQuery) {
          // Provide a different response for repeat queries
          response = "I've already provided that information. Is there something specific about these ingredients you'd like to know more about?";

        } else if (currentMessage.toLowerCase().includes('harmful') || 
                   currentMessage.toLowerCase().includes('bad') ||
                   currentMessage.toLowerCase().includes('dangerous')) {
          const harmfulIngredients = productInfo.ingredients.filter(i => i.concerns.length > 0);
          if (harmfulIngredients.length > 0) {
            response = `I found ${harmfulIngredients.length} ingredients with potential concerns:\n` +
              harmfulIngredients.map(i => `• ${i.name}: ${i.concerns.join(', ')}`).join('\n');
          } else {
            response = "I didn't identify any ingredients with known concerns in this product.";
          }
        } else if (currentMessage.toLowerCase().includes('ingredient')) {
          response = `This product contains ${productInfo.ingredients.length} ingredients:\n` +
            productInfo.ingredients.map(i => `• ${i.name}`).join('\n');
        } else if (currentMessage.toLowerCase().includes('purpose') || 
                   currentMessage.toLowerCase().includes('what does') || 
                   currentMessage.toLowerCase().includes('what is') ||
                   currentMessage.toLowerCase().includes('do they do')) {
          response = "Here's what each ingredient does:\n" +
            productInfo.ingredients.map(i => `• ${i.name}: ${i.purpose}`).join('\n');
        } else if (currentMessage.toLowerCase().includes('gemini') || 
                   currentMessage.toLowerCase().includes('ai') ||
                   currentMessage.toLowerCase().includes('model')) {
          response = "This is a demo simulation of what the Gemini-powered chat would be like. In the production version, this would connect to Gemini API for more sophisticated and accurate responses about beauty product ingredients.";
        } else if (currentMessage.toLowerCase().includes('safe') ||
                   currentMessage.toLowerCase().includes('good for')) {
          const safeIngredients = productInfo.ingredients.filter(i => i.concerns.length === 0);
          response = `${safeIngredients.length} out of ${productInfo.ingredients.length} ingredients have no known concerns:\n` +
            safeIngredients.map(i => `• ${i.name}: ${i.purpose}`).join('\n');
        } else {
          // Generic response with some variation
          const genericResponses = [
            `I can tell you about the ingredients in ${productInfo.name}. You can ask if any ingredients are harmful, what each ingredient does, or for a full ingredient list.`,
            `What would you like to know about the ingredients in ${productInfo.name}? I can identify harmful ingredients, explain purposes, or show you the full list.`,
            `I'm here to help you understand what's in ${productInfo.name}. Ask me about specific ingredients, their purposes, or potential concerns.`
          ];
          response = genericResponses[Math.floor(Math.random() * genericResponses.length)];
        }
      }

      // Add assistant response to chat
      setChatMessages(prev => [
        ...prev,
        { role: 'assistant', content: response }
      ]);

      setIsLoading(false);
    }, 1500);
  };

  return (
    <section className="ingredients-container">
      <h1>Ingredient Checker</h1>
      <p>Upload a product image with a barcode to check ingredients and ask questions about them.</p>

      <div className="scanner-chat-layout">
        <div className="scanner-section">
          <div id="scanner-container" className="scanner-container" style={{display: 'none'}}></div>

          <div className="upload-container">
            {!imagePreview ? (
              <div className="upload-box">
                <label htmlFor="image-upload" className="upload-label">
                  <div className="upload-icon">
                    <svg xmlns="http://www.w3.org/2000/svg" width="48" height="48" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                      <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"></path>
                      <polyline points="17 8 12 3 7 8"></polyline>
                      <line x1="12" y1="3" x2="12" y2="15"></line>
                    </svg>
                  </div>
                  <p>Click to upload a product image with barcode</p>
                </label>
                <input 
                  type="file" 
                  id="image-upload" 
                  accept="image/*" 
                  onChange={handleImageChange} 
                  style={{ display: 'none' }}
                />
              </div>
            ) : (
              <div className="image-preview-container">
                <img src={imagePreview} alt="Preview" className="image-preview" />
                <div className="preview-actions">
                  <button 
                    className="analyze-btn" 
                    onClick={handleScanBarcode}
                    disabled={isScanning}
                  >
                    {isScanning ? 'Scanning Barcode...' : 'Scan Barcode'}
                  </button>
                  <button className="clear-btn" onClick={handleClearImage}>
                    Clear
                  </button>
                </div>
              </div>
            )}
          </div>

          {barcode && (
            <div className="barcode-result">
              <p>Barcode: {barcode}</p>
              {productInfo && (
                <div className="product-info">
                  <h3>{productInfo.name}</h3>
                  <p>Brand: {productInfo.brand}</p>
                </div>
              )}
            </div>
          )}
        </div>

        <div className="chat-section">
          <h2>Beauty Buddy</h2>
          <p>Ask about the ingredients in your scanned products</p>

          <div className="chat-container" ref={chatContainerRef}>
            <div className="chat-messages">
              {chatMessages.map((message, index) => (
                <div key={index} className={`message ${message.role}`}>
                  {message.content}
                </div>
              ))}
              {isLoading && (
                <div className="message assistant loading">
                  <span className="loading-dot"></span>
                  <span className="loading-dot"></span>
                  <span className="loading-dot"></span>
                </div>
              )}
            </div>
          </div>

          <form onSubmit={handleChatSubmit} className="chat-input-form">
            <input
              type="text"
              value={userMessage}
              onChange={(e) => setUserMessage(e.target.value)}
              placeholder="Ask about the ingredients..."
              className="chat-input"
            />
            <button type="submit" className="chat-submit" disabled={isLoading}>
              Send
            </button>
          </form>
        </div>
      </div>
    </section>
  );
}
